package oct_14_2021;

public class Arithmetic_Operators_Example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=50,y=3;
		System.out.println("Arithmetic operator Example");
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);
		System.out.println(x%y);
	}

}
