package oct_14_2021;

public class Arithmetic_Operators_Expression {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5,y=19,z=4;
		System.out.println("Arthimetic Operators using Expression");
		System.out.println(z%x/y);
	//	System.out.println(x/y);
		//System.out.println(z%0);
		System.out.println(x+y*z%x/y-z*x);
		System.out.println(x+y*z%x/y-z*x);
		System.out.println(x+y*z%x/y-z*x);
	}

}
